mapper.select:
/* 자사 직책 관리 > 조회 */
SELECT job_id , job_title , min_salary , max_salary FROM hr.jobs
AND job_title LIKE '%'||#{jobTitle, jdbcType=VARCHAR}||'%'

mapper.insert:
/* 자사 직책 관리 > 등록 */
INSERT INTO hr.jobs (
            job_id
          , job_title
          , min_salary
          , max_salary
 ) VALUES ( 
            #{jobId    , jdbcType=VARCHAR}
          , #{jobTitle , jdbcType=VARCHAR}
          , #{minSalary, jdbcType=NUMERIC}
          , #{maxSalary, jdbcType=NUMERIC}
 )

mapper.update:
/* 자사 직책 관리 > 수정 */
UPDATE hr.jobs
   SET job_title  = #{jobTitle , jdbcType=VARCHAR}
     , min_salary = #{minSalary, jdbcType=NUMERIC}
     , max_salary = #{maxSalary, jdbcType=NUMERIC}
 WHERE job_id     = #{jobId    , jdbcType=VARCHAR}

mapper.delete:
/* 자사 직책 관리 > 삭제 */
DELETE 
  FROM hr.jobs
 WHERE job_id = #{jobId, jdbcType=VARCHAR}

mapper.select:
/* 자사 직책 관리 > PK */
SELECT COUNT(*) AS cnt
  FROM hr.jobs
 WHERE job_id = #{jobId, jdbcType=VARCHAR}
