mapper.select:
/* 국가 관리 > 조회 */
SELECT cunt.region_id , (SELECT region_name FROM hr.regions reg WHERE reg.region_id = cunt.region_id) AS region_name , cunt.country_id , cunt.country_name FROM hr.countries cunt
AND region_id = #{regionId, jdbcType=NUMERIC}

mapper.insert:
/* 국가 관리 > 등록 */
INSERT INTO hr.countries (
            country_id
          , country_name
          , region_id
 ) VALUES ( 
            #{countryId  , jdbcType=VARCHAR}
          , #{countryName, jdbcType=VARCHAR}
          , #{regionId   , jdbcType=NUMERIC}
 )

mapper.update:
/* 국가 관리 > 수정 */
UPDATE hr.countries
   SET country_name = #{countryName, jdbcType=VARCHAR}
     , region_id    = #{regionId   , jdbcType=NUMERIC}
 WHERE country_id   = #{countryId  , jdbcType=VARCHAR}

mapper.delete:
/* 국가 관리 > 삭제 */
DELETE 
  FROM hr.countries
 WHERE country_id = #{countryId, jdbcType=VARCHAR}

mapper.select:
/* 국가 관리 > PK */
SELECT COUNT(*) AS cnt
  FROM hr.countries
 WHERE country_id = #{countryId, jdbcType=VARCHAR}
