mapper.select:
/* ID 검증 */
SELECT COUNT(*) AS id_cnt
  FROM (
        SELECT '20220001' as user_id
          FROM DUAL
       )
 WHERE user_id = #{userId, jdbcType=VARCHAR}

/* LOGIN 검증 */
SELECT * 
  FROM (
        SELECT '20220001' as user_id
             , 'a123456789' as user_pw
             , '홍길동' as user_nm
          FROM DUAL
       )
 WHERE user_id = #{userId, jdbcType=VARCHAR} 
   AND user_pw = #{userPw, jdbcType=VARCHAR}
