mapper.select:
/* 관리자 부서 관리 > 조회 */
SELECT department_id , department_name , manager_id , (SELECT emp.first_name||' '||emp.last_name FROM hr.employees emp WHERE emp.employee_id = dept.manager_id) AS manager_name , dept.location_id , loc.city AS location_name FROM hr.departments dept LEFT JOIN hr.locations loc ON dept.location_id = loc.location_id
AND department_name LIKE '%'||#{departmentName, jdbcType=VARCHAR}||'%'
AND loc.country_id = #{countryId, jdbcType=VARCHAR}

mapper.insert:
/* 관리자 부서 관리 > 등록 */
INSERT INTO hr.departments (
            department_id
          , department_name
          , manager_id
          , location_id
 ) VALUES ( 
            hr.departments_seq.NEXTVAL
          , #{departmentName, jdbcType=VARCHAR}
          , #{managerId     , jdbcType=NUMERIC}
          , #{locationId    , jdbcType=NUMERIC}
 )

mapper.update:
/* 관리자 부서 관리 > 수정 */
UPDATE hr.departments
   SET department_name = #{departmentName, jdbcType=VARCHAR}
     , manager_id      = #{managerId     , jdbcType=NUMERIC}
     , location_id     = #{locationId    , jdbcType=NUMERIC}
 WHERE department_id   = #{departmentId  , jdbcType=NUMERIC}

mapper.delete:
/* 관리자 부서 관리 > 삭제 */
DELETE 
  FROM hr.departments
 WHERE department_id = #{departmentId, jdbcType=NUMERIC}

mapper.select:
/* 관리자 부서 관리 > PK */
SELECT COUNT(*) AS cnt
  FROM hr.departments
 WHERE department_id = #{departmentId, jdbcType=NUMERIC}
