mapper.select:
/* 관리자 사원 관리 > 조회 */
SELECT employee_id , first_name , last_name , first_name||' '||last_name AS employee_name , email , phone_number , hire_date , job_id , (SELECT j.job_title FROM hr.jobs j WHERE j.job_id = emp.job_id) AS job_title , salary , commission_pct , manager_id , (SELECT e.first_name||' '||e.last_name FROM hr.employees e WHERE e.employee_id = emp.manager_id) AS manager_name , department_id , (SELECT d.department_name FROM hr.departments d WHERE d.department_id = emp.department_id) AS department_name FROM hr.employees emp
AND department_id = #{departmentId, jdbcType=NUMERIC}
AND job_id = #{jobId , jdbcType=VARCHAR}

mapper.insert:
/* 관리자 사원 관리 > 등록 */
INSERT INTO hr.employees (
            employee_id
          , first_name
          , last_name
          , email
          , phone_number
          , hire_date
          , job_id
          , salary
          , commission_pct
          , manager_id
          , department_id
 ) VALUES ( 
            hr.employees_seq.NEXTVAL
          , #{firstName    , jdbcType=VARCHAR}
          , #{lastName     , jdbcType=VARCHAR}
          , #{email        , jdbcType=VARCHAR}
          , #{phoneNumber  , jdbcType=VARCHAR}
          , TO_DATE(#{hireDate, jdbcType=VARCHAR}, 'yyyy-MM-dd hh24:mi:ss')
          , #{jobId        , jdbcType=VARCHAR}
          , #{salary       , jdbcType=NUMERIC}
          , #{commissionPct, jdbcType=NUMERIC}
          , #{managerId    , jdbcType=NUMERIC}
          , #{departmentId , jdbcType=NUMERIC}
 )

mapper.update:
 /* 관리자 사원 관리 > 수정 */
 UPDATE hr.employees
    SET first_name     = #{firstName    , jdbcType=VARCHAR}
      , last_name      = #{lastName     , jdbcType=VARCHAR}
      , email          = #{email        , jdbcType=VARCHAR}
      , phone_number   = #{phoneNumber  , jdbcType=VARCHAR}

    
      , job_id         = #{jobId        , jdbcType=VARCHAR}
    
    
      , department_id  = #{departmentId , jdbcType=NUMERIC}
    
      , hire_date      = SYSDATE

      , salary         = #{salary       , jdbcType=NUMERIC}
      , commission_pct = #{commissionPct, jdbcType=NUMERIC}
      , manager_id     = #{managerId    , jdbcType=NUMERIC}
  WHERE employee_id    = #{employeeId   , jdbcType=NUMERIC}

mapper.delete:
/* 관리자 사원 관리 > 삭제 */
DELETE 
  FROM hr.employees
 WHERE employee_id = #{employeeId, jdbcType=NUMERIC}

mapper.select:
/* 관리자 사원 관리 > PK */
SELECT COUNT(*) AS cnt
  FROM hr.employees
 WHERE employee_id = #{employeeId, jdbcType=NUMERIC}

/* 관리자 사원 관리 > Email Check */
SELECT COUNT(*) AS cnt
  FROM hr.employees
 WHERE email = #{email, jdbcType=VARCHAR}

/* 관리자 사원 관리 > 사원 삭제 Job History Data Check */
SELECT COUNT(*) AS cnt
  FROM hr.job_history
 WHERE employee_id = #{employeeId, jdbcType=NUMERIC}
