mapper.select:
/* 부서 위치 관리 > 조회 */
SELECT loc.country_id , (SELECT cunt.country_name FROM hr.countries cunt WHERE cunt.country_id = loc.country_id) AS country_name , loc.location_id , loc.city , loc.postal_code , loc.state_province , loc.street_address FROM hr.locations loc
AND loc.city LIKE '%'||#{city, jdbcType=VARCHAR}||'%'
AND loc.country_id = #{countryId, jdbcType=VARCHAR}

mapper.insert:
/* 부서 위치 관리 > 등록 */
INSERT INTO hr.locations (
            location_id
          , postal_code
          , state_province
          , city
          , street_address
          , country_id
 ) VALUES ( 
            hr.locations_seq.NEXTVAL
          , #{postalCode    , jdbcType=VARCHAR}
          , #{stateProvince , jdbcType=VARCHAR}
          , #{city          , jdbcType=VARCHAR}
          , #{streetAddress , jdbcType=VARCHAR}
          , #{countryId     , jdbcType=VARCHAR}
 )

mapper.update:
/* 부서 위치 관리 > 수정 */
UPDATE hr.locations
   SET postal_code    = #{postalCode    , jdbcType=VARCHAR}
     , state_province = #{stateProvince , jdbcType=VARCHAR}
     , city           = #{city          , jdbcType=VARCHAR}
     , street_address = #{streetAddress , jdbcType=VARCHAR}
     , country_id     = #{countryId     , jdbcType=VARCHAR}
 WHERE location_id    = #{locationId    , jdbcType=NUMERIC}

mapper.delete:
/* 부서 위치 관리 > 삭제 */
DELETE 
  FROM hr.locations
 WHERE location_id = #{locationId, jdbcType=NUMERIC}

mapper.select:
/* 부서 위치 관리 > PK */
SELECT COUNT(*) AS cnt
  FROM hr.locations
 WHERE location_id = #{locationId, jdbcType=NUMERIC}
